import fetch from 'isomorphic-fetch'

// let posts = [
//   {
//     id: 1
//   },
//   {
//     id: 2
//   }
// ]

 let posts = fetch('https://jsonplaceholder.typicode.com/posts')

    .then(response => response.json())
    .then(data => {
      return {posts: data}
    }
  )




export default (req, res) => {
  res.status(200).json(posts)
}
