import 'isomorphic-fetch';

const Product = ({productImages}) => {
  console.log(product)
  const { product_id, id, url_tiny } = productImages
  console.log(id)

  return (
    <div>
      {
        productImages.map(productImage => <div>{product_id} - {id} - <img src={url_tiny} alt=""/> </div>)
      }

    </div>
  )
}

// call api to get sinlge product data

Product.getInitialProps = async function(context){
  console.log('id = ', context.query.id)
  const res = await fetch(`http://localhost:3000/api/products/${context.query.id}`)
  const data = await res.json()
  return {
    productImages: data
  }

}

export default Product
