import 'isomorphic-fetch';

import Link from 'next/link'

const Products2 = (props) => {
  console.log(props.products)
  const { products} = props

  return (
    <div>
      {/* <Link href="/products2/[id]">
  <a> ... </a>
</Link> */}
      Products
      {
        products && products.map(product => 
          <Link href="/products2/{product.id}" as="">
          <div key={product.id}>{product.id} - {product.name}</div>
        </Link>
        )



      }
    </div>
  )
}

Products2.getInitialProps = async function(){
  const res = await fetch('http://localhost:3000/api/products2')
  const productsData = await res.json()
  return {
    products: productsData
  }

}


export default Products2
