import fetch from 'isomorphic-fetch'

let products = fetch(`https://api.bigcommerce.com/stores/5xucklva8y/v3/catalog/products/${id}`,
{
  method: "GET",
    headers: {
      "X-Auth-Client": '4berj6c79aulu1fiqgeq3kupga0oe01',
      "X-Auth-Token": '5xe20syvcw3l4oibycyeoj85ezsvaio'
    }
}
)
.then(response => response.json())
.then(data => {
  return products = data.data
}
)

export default (req, res) => {
  res.status(200).json({id: req.query.id, products})
}

